section.listing:
{ заголовок }
let ident = regexp ...
rule entrypoint =
  parse regexp { action }
      | ...
      | regexp { action }
and entrypoint =
  parse ...
and ...
{ концевик }

subsection.listing:
нетерминальные : символ ... символ {
	  семантическое действие } | ... | символ ... символ {
	  семантическое действие } ;

section.listing:
/* Файл parser.mly */
%token <int> INT
%token PLUS MINUS TIMES DIV
%token LPAREN RPAREN
%token EOL
%left PLUS MINUS        /* низший приоритет */
%left TIMES DIV         /* средний приоритет */
%nonassoc UMINUS        /* высший приоритет */
%start main             /* точка входа */
%type <int> main
%%
main:
  expr EOL                { $1 }
;
expr:
   INT                     { $1 }
 | LPAREN expr RPAREN      { $2 }
 | expr PLUS expr          { $1 + $3 }
 | expr MINUS expr         { $1 - $3 }
 | expr TIMES expr         { $1 * $3 }
 | expr DIV expr           { $1 / $3 }
 | MINUS expr %prec UMINUS { - $2 }
;

(* Файл lexer.mll *)
{
open Parser        (* Тип token определен в parser.mli *)
exception Eof
}
rule token = parse
   [' ' '\t']     { token lexbuf }     (* пропускаем пробельные символы *)
 | ['\n' ]        { EOL }
 | ['0'-'9']+     { INT(int_of_string(Lexing.lexeme lexbuf)) }
 | '+'            { PLUS }
 | '-'            { MINUS }
 | '*'            { TIMES }
 | '/'            { DIV }
 | '('            { LPAREN }
 | ')'            { RPAREN }
 | eof            { raise Eof }

dict-art.listing:
rule token = parse
  "keyword1"   { KWD1 }
| "keyword2"   { KWD2 }
| ...
| "keyword100" { KWD100 }
| ['A'-'Z' 'a'-'z'] ['A'-'Z' 'a'-'z' '0'-'9' '_'] *
               { IDENT(Lexing.lexeme lexbuf) }
